import React, { Component } from 'react';

class UserForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      firstName: '',
      lastName: '',
      email: '',
      phoneNumber: '',
      gender: 'Male',
    };
  }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }

  handleSubmit = (event) => {
    event.preventDefault();
    console.log(this.state);
  }

  render() {
    return (
      <div className="container text-center"> {/* Center the form horizontally and vertically */}
        <div className="form-container"> 
          <h2>NetServ Form</h2>
          <form onSubmit={this.handleSubmit}>
            <div className="mb-3">
              <label className="form-label label-text">Username</label>
              <input
                type="text"
                // className="form-control"
                name="username"
                value={this.state.username}
                onChange={this.handleChange}
              />
            </div>
            <div className="mb-3">
              <label className="form-label label-text">First Name</label>
              <input
                type="text"
                // className="form-control"
                name="firstName"
                value={this.state.firstName}
                onChange={this.handleChange}
              />
            </div>
            <div className="mb-3">
              <label className="form-label label-text">Last Name</label>
              <input
                type="text"
                // className="form-control"
                name="lastName"
                value={this.state.lastName}
                onChange={this.handleChange}
              />
            </div>
            <div className="mb-3">
              <label className="form-label label-text">Email</label>
              <input
              type="email"
              // className="form-control"
              name='Email'
              value={this.state.email}
              onChange={this.handleChange}
              />
              </div>
              <div className="mb-3">
              <label className="form-label label-text">PhoneNo</label>
              <input
              type="text"
              // className="form-control"
              name='phoneNo'
              value={this.state.phoneNo}
              onChange={this.handleChange}
              />
              </div>
              
              <div className="mb-3">
            <label className="form-label label-text">Gender</label>
            <select
              // className="form-select"
              name="gender"
              value={this.state.gender}
              onChange={this.handleChange}
            >
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Other">Other</option>
            </select>
          </div>
            <button type="submit" className="btn btn-primary">Submit</button>
          </form>
        </div>
      </div>
    );
  }
}

export default UserForm;
